import styled from 'styled-components';
import React, {useEffect, useRef, useState} from "react";
import bkimg from "../assets/bkimg.jpg";
import logo from "../assets/logo.jpg";
import MypageCancelbtn from '../buttons/mypagecancelbtn'
import Mypage_myinfobtn from '../buttons/mypage_myinfobtn'
import Mypage_infoeditbtn from '../buttons/mypage_infoeditbtn'
import Mypage_goodsentencebtn from '../buttons/mypage_goodsentencebtn'
// import { getSentences } from '../sentenceapi'

const Container = styled.div`
position: absolute;
width: 100%;
height: 100%;
z-index: 1;
background-image: url(${bkimg});
background-size: cover;
text-align: center;

`;

const Logo = styled.img.attrs({
src: `${logo}`,
})`
position: absolute;
width: 30%;
height: 17%;
top: 5%;
left: 3%;
border-radius: 1rem;
`
const MenuContainer = styled.div`
position: absolute;
width: 30%;
height: 68%;
top: 27%;
left: 3%;
background-color: rgba(255, 255, 255, 0.5);
border-radius: 1rem;
`
const SummaryInfo = styled.div`
position: absolute;
width: 35%;
height: 17%;
top: 5%;
left: 5%;
background-color: rgba(127, 127, 127, 0.5);
border-radius: 1rem;
`
const DetailInfo = styled.div`
position: absolute;
width: 85%;
height: 65%;
top: 29%;
left: 5%;
background-color: rgba(127, 127, 127, 0.5);
border-radius: 1rem;
`
const ResultContainer = styled.div`
position: absolute;
width: 60%;
height: 77%;
top: 5%;
right: 3%;
background-color: rgba(255, 255, 255, 0.5);
border-radius: 1rem;
`


const Signoutbtn = styled.button`
position: absolute;
width: 13%;
height: 8%;
bottom: 5%;
right: 19%;
border-radius: 1rem;
font-size: 2rem;
text-align: center;
`
export default function Goodsentence() {

  const [data, setData] = useState([]);
  const dataId = useRef(0);

  const getData = async () => {
    const res = await fetch(
      "https://jsonplaceholder.typicode.com/comments"
    ).then((res) => res.json());

    const initData = res.slice(0, 20).map((it) => {
      return {
        author: it.email,
        content: it.body,
        emotion: Math.floor(Math.random() * 5) + 1,
        created_date: new Date().getTime(),
        id: dataId.current++,
      };
    });
    setData(initData);
  };

  useEffect(() => {
    getData();
  }, [data]);

  return (

    <div className='container'>
      <Container>
        <div className = 'logo'><Logo/></div>
        <div className = 'menucontainer'><MenuContainer>
          <Mypage_myinfobtn/>
          <Mypage_infoeditbtn/>
          <Mypage_goodsentencebtn/>
          </MenuContainer></div>
        <div className = 'resultcontainer'><ResultContainer>
          <SummaryInfo/>
          <DetailInfo>{data}</DetailInfo>
          </ResultContainer>
          </div>
        <div className = 'signout'><Signoutbtn>회원탈퇴</Signoutbtn></div>
        <div className = 'mypagecancel'><MypageCancelbtn/></div>
      </Container>
    </div>
  )
  }
