import styled from 'styled-components';
import React from "react";
import bkimg from "../assets/bkimg.jpg";
import logo from "../assets/logo.jpg";


export default function Login() {

  const Container = styled.div`
  position: absolute;
  width: 100%;
  height: 100%;
  z-index: 1;
  background-image: url(${bkimg});
  background-size: cover;
  text-align: center;
  `;

  const Logo = styled.img.attrs({
    src: `${logo}`,
  })`
  position: absolute;
  width: 30%;
  height: 17%;
  top: 23%;
  left: 35%;
  border-radius: 1rem;
  `
  const Input_id = styled.input`
  position: absolute;
  width: 18%;
  height: 13%;
  top: 45%;
  left: 41%;
  border-radius: 2rem;
  border-color: brown;
  font-size: 3rem;
  text-align: center;
  background-color: rgba(255, 255, 255, 0.5);
  `
  const Input_password = styled.input`
  position: absolute;
  width: 18%;
  height: 13%;
  top: 63%;
  left: 41%;
  border-radius: 2rem;
  border-color: brown;
  font-size: 3rem;
  justify-content: center;
  text-align: center;
  background-color: rgba(255, 255, 255, 0.5)
  `

  const Loginbtn2 = styled.button`
  position: absolute;
  width: 8%;
  height: 6%;
  top: 47%;
  left: 44.5%;
  border-radius: 1rem;
  font-size: 3rem;
  text-align: center;
  `

  return (

    <div className='container'>
      <Container>
        <div className = 'logo'><Logo/></div>
        <div className='input_id'>
        <Input_id placeholder='아이디를 입력하세요'/></div>
        <div className='input_password'><Input_password placeholder='비밀번호를 입력하세요'/></div>

      </Container>
    </div>
  )
}
